function appendModalMenu(e){let t=e.currentTarget,n=modalElement.cloneNode(!0);n.classList.remove("_non-active"),t.append(n),setTimeout(()=>{n.classList.add("_active")},30)}function removeModalMenu(e){let t=e.currentTarget.lastElementChild;t.classList.contains("class")&&(t.classList.remove("_active"),setTimeout(()=>{t.remove()},200))}const contentElements=document.querySelectorAll(".container-class"),modalElement=document.querySelector(".modal");