export default class ElementModal{contentElements;modalElement;constructor(e,t,n){if(!(e&&t&&n>=0))throw"[ELEMENT-MODAL] The specified elements were not found!";this.contentElements=document.querySelectorAll(e),this.modalElement=document.querySelector(t);for(const e of this.contentElements)e.addEventListener("mouseenter",()=>{this.appendModalMenu(e,this.modalElement)}),e.addEventListener("mouseleave",()=>{this.removeModalMenu(e,n)})}appendModalMenu(e,t){let n=t.cloneNode(!0);e.append(n),setTimeout(()=>{n.classList.remove("_non-active")},30)}removeModalMenu(e,t){let n=e.lastElementChild;n&&(n.classList.add("_non-active"),setTimeout(()=>{n.remove()},t+100))}}