export default class Filter{filterButtons=NodeList;filterContentElements=NodeList;constructor(t,e){if(!(t.length>0&&e.length>0))throw"Length of filter elements must be more than zero.";this.filterButtons=t,this.filterContentElements=e;for(const t of this.filterButtons)t.addEventListener("click",()=>{Filter.filtContentByType(t,this.filterContentElements)})}static filtContentByType(t,e){let n=t.dataset.filtContent;for(const t of e)"all"===n||t.dataset.contentType===n?t.style.display="":t.style.display="none"}}