let body=document.body;export default class ModalWindowMenu{static modalLinks;static modalClosers;static FS_MENU_CLASSLIST;static UNLOCK=!0;constructor(){ModalWindowMenu.FS_MENU_CLASSLIST=document.querySelector(".fullscreen-navmenu").classList,ModalWindowMenu.modalLinks=document.querySelectorAll("[data-modal-link]");for(let o of ModalWindowMenu.modalLinks)o.addEventListener("click",()=>{let e=o.dataset.modalLink;if(void 0!==e){let o=document.getElementById(e);this.showOrHideModal(o)}});ModalWindowMenu.modalClosers=document.querySelectorAll(".modal-closer");for(const o of ModalWindowMenu.modalClosers)o.addEventListener("click",()=>{this.closeModal(o.closest(".modal-window"),!0)});document.addEventListener("keydown",o=>{if("Escape"===o.code){let o=document.querySelector(".modal-window.active");this.closeModal(o,!0)}})}showOrHideModal(o){if(o&&ModalWindowMenu.UNLOCK){let e=document.querySelector(".modal-window.active");e?this.closeModal(e,!1):this.toggleBodyScroll(!1),o.classList.add("active")}o.addEventListener("click",e=>{e.target.closest(".modal-window__content")||this.closeModal(o,!0)})}closeModal(o,e){ModalWindowMenu.UNLOCK&&(o.classList.remove("active"),e&&this.toggleBodyScroll(!0))}toggleBodyScroll(o){o&&!ModalWindowMenu.FS_MENU_CLASSLIST.contains("active")?(body.style.paddingRight=0,body.classList.remove("scroll-block")):(body.style.paddingRight=returnScrollbarWidth()+"px",body.classList.add("scroll-block")),ModalWindowMenu.UNLOCK=!1,setTimeout(()=>{ModalWindowMenu.UNLOCK=!0},1e3*transitionTimeout)}}function returnScrollbarWidth(){return window.innerWidth-document.querySelector("html").clientWidth}const transitionTimeout=.5;