export default class ScrollElement{static FIXED_HEADER_HEIGHT=0;constructor(e,t){let l=document.querySelectorAll(e);if(!(l.length>=0))throw"[SCROLL-ELEMENTS] The specified elements were not found!";for(let e of l)e.addEventListener("click",()=>{this.scrollToElement(e)});if(t&&""!=t){let e=document.querySelector(t).clientHeight;ScrollElement.FIXED_HEADER_HEIGHT=e}}scrollToElement(e){let t=document.querySelector(""+e.dataset.scrollTo);if(t){let e=window.pageYOffset+t.getBoundingClientRect().top;window.scrollTo({top:e-ScrollElement.FIXED_HEADER_HEIGHT,behavior:"smooth"})}}}