export default class SidebarMenu{sidebars;sidebarButtons;constructor(e,t){if(!e||!t)throw"[SIDEBAR] Length of sidebar elements must be more than zero.";this.sidebars=document.querySelectorAll(e),this.sidebarButtons=document.querySelectorAll(t);for(const e of this.sidebarButtons)e.addEventListener("click",()=>{this.toggleSidebar(e)})}toggleSidebar(e){let t=document.getElementById(e.dataset.openSidebar);e.classList.toggle("active"),t.classList.toggle("active")}}